mapper.select:
select
w.id,
w.scores_id,
w.ap_sat_weight,
w.ap_act_weight,
w.ib_sat_weight,
w.ib_act_weight,
w.school_id,
sas.id,
sas.toeft_low_req,
sas.ielts_low_req,
sas.school_id,
sas.tuition_fees,
sas.nineteen,
sas.first_students_num
from d_school_admission_scores sas ,d_weight w
where w.scores_id = sas.id


        and sas.tuition_fees < #{tuitionFees} and sas.first_students_num > #{required}
        and sas.nineteen > 50

            and sas.toeft_low_req <= #{toeflHigh} and sas.toeft_low_req >= #{toeflLow}


            and sas.ielts_low_req <= #{toeflHigh} and sas.ielts_low_req >= #{toeflLow}

        and w.ib_act_weight >= #{ibActWeightMin} and w.ib_act_weight <= #{ibActWeightMax}


        and sas.tuition_fees < #{tuitionFees}

            and sas.toeft_low_req <= #{toeflHigh} and sas.toeft_low_req >= #{toeflLow}


            and sas.ielts_low_req <= #{toeflHigh} and sas.ielts_low_req >= #{toeflLow}

        and w.ib_act_weight >= #{ibActWeightMin} and w.ib_act_weight <= #{ibActWeightMax}


        and sas.tuition_fees < #{tuitionFees} and sas.first_students_num > #{required}

            and sas.toeft_low_req <= #{toeflHigh} and sas.toeft_low_req >= #{toeflLow}


            and sas.ielts_low_req <= #{toeflHigh} and sas.ielts_low_req >= #{toeflLow}

        and w.ib_act_weight >= #{ibActWeightMin} and w.ib_act_weight <= #{ibActWeightMax}




        and sas.tuition_fees < #{tuitionFees} and sas.first_students_num > #{required}
        and sas.nineteen > 50

            and sas.toeft_low_req <= #{toeflHigh} and sas.toeft_low_req >= #{toeflLow}


            and sas.ielts_low_req <= #{toeflHigh} and sas.ielts_low_req >= #{toeflLow}

        and w.ap_act_weight >= #{apActWeightMin} and w.ap_act_weight <= #{apActWeightMax}


        and sas.tuition_fees < #{tuitionFees}

            and sas.toeft_low_req <= #{toeflHigh} and sas.toeft_low_req >= #{toeflLow}


            and sas.ielts_low_req <= #{toeflHigh} and sas.ielts_low_req >= #{toeflLow}

        and w.ap_act_weight >= #{apActWeightMin} and w.ap_act_weight <= #{apActWeightMax}


        and sas.tuition_fees < #{tuitionFees} and sas.first_students_num > #{required}

            and sas.toeft_low_req <= #{toeflHigh} and sas.toeft_low_req >= #{toeflLow}


            and sas.ielts_low_req <= #{toeflHigh} and sas.ielts_low_req >= #{toeflLow}

        and w.ap_act_weight >= #{apActWeightMin} and w.ap_act_weight <= #{apActWeightMax}





        and sas.tuition_fees < #{tuitionFees} and sas.first_students_num > #{required}
        and sas.nineteen > 50

             and sas.toeft_low_req <= #{toeflHigh} and sas.toeft_low_req >= #{toeflLow}


             and sas.ielts_low_req <= #{toeflHigh} and sas.ielts_low_req >= #{toeflLow}

         and w.ib_sat_weight >= #{ibSatWeightMin} and w.ib_sat_weight <= #{ibSatWeightMax}


        and sas.tuition_fees < #{tuitionFees}

            and sas.toeft_low_req <= #{toeflHigh} and sas.toeft_low_req >= #{toeflLow}


            and sas.ielts_low_req <= #{toeflHigh} and sas.ielts_low_req >= #{toeflLow}

        and w.ib_sat_weight >= #{ibSatWeightMin} and w.ib_sat_weight <= #{ibSatWeightMax}


        and sas.tuition_fees < #{tuitionFees} and sas.first_students_num > #{required}

            and sas.toeft_low_req <= #{toeflHigh} and sas.toeft_low_req >= #{toeflLow}


            and sas.ielts_low_req <= #{toeflHigh} and sas.ielts_low_req >= #{toeflLow}

        and w.ib_sat_weight >= #{ibSatWeightMin} and w.ib_sat_weight <= #{ibSatWeightMax}




        and sas.tuition_fees < #{tuitionFees} and sas.first_students_num > #{required}
        and sas.nineteen > 50

            and sas.toeft_low_req <= #{toeflHigh} and sas.toeft_low_req >= #{toeflLow}


            and sas.ielts_low_req <= #{toeflHigh} and sas.ielts_low_req >= #{toeflLow}

        and w.ap_sat_weight >= #{apSatWeightMin} and w.ap_sat_weight <= #{apSatWeightMax}


        and sas.tuition_fees < #{tuitionFees}

            and sas.toeft_low_req <= #{toeflHigh} and sas.toeft_low_req >= #{toeflLow}


            and sas.ielts_low_req <= #{toeflHigh} and sas.ielts_low_req >= #{toeflLow}

        and w.ap_sat_weight >= #{apSatWeightMin} and w.ap_sat_weight <= #{apSatWeightMax}


        and sas.tuition_fees < #{tuitionFees} and sas.first_students_num > #{required}

            and sas.toeft_low_req <= #{toeflHigh} and sas.toeft_low_req >= #{toeflLow}


            and sas.ielts_low_req <= #{toeflHigh} and sas.ielts_low_req >= #{toeflLow}

        and w.ap_sat_weight >= #{apSatWeightMin} and w.ap_sat_weight <= #{apSatWeightMax}


order by sas.nineteen asc
