mapper.select:
select
w.id,
w.scores_id,
w.ap_sat_weight,
w.ap_act_weight,
w.ib_sat_weight,
w.ib_act_weight,
w.school_id,
sas.id,
sas.toeft_low_req,
sas.ielts_low_req,
sas.school_id
from d_school_admission_scores sas ,d_weight w
where w.scores_id = sas.id

    and sas.toeft_low_req <= #{toeflHigh} and sas.toeft_low_req >= #{toeflLow}
    and w.ib_act_weight >= #{ibActWeightMin} and w.ib_act_weight <= #{ibActWeightMax}


    and sas.toeft_low_req < #{toeflHigh} and sas.toeft_low_req > #{toeflLow}
    and w.ap_act_weight >= #{apActWeightMin} and w.ap_act_weight <= #{apActWeightMax}


    and sas.toeft_low_req < #{toeflHigh} and sas.toeft_low_req > #{toeflLow}
    and w.ib_sat_weight >= #{ibSatWeightMin} and w.ib_sat_weight <= #{ibSatWeightMax}


    and sas.toeft_low_req <= #{toeflHigh} and sas.toeft_low_req >= #{toeflLow}
    and w.ap_sat_weight >= #{apSatWeightMin} and w.ap_sat_weight <= #{apSatWeightMax}

order by w.ib_act_weight desc ,w.ap_sat_weight desc,w.ib_sat_weight desc,w.ap_act_weight desc
